INSERT INTO Recipes (title, ingredients, instructions, external_id, user_id) VALUES
('Spaghetti Carbonara', '- 200g spaghetti\n- 100g pancetta\n- 2 large eggs\n- 50g grated Parmesan cheese\n- Salt and pepper to taste', '1. Cook spaghetti in salted boiling water until al dente.\n2. Fry pancetta until crisp in a pan.\n3. Beat eggs in a bowl and mix in Parmesan cheese.\n4. Toss the drained pasta with pancetta, then mix in the egg and cheese mixture off the heat.\n5. Serve immediately, seasoned with salt and pepper.', NULL, 1),
('Chicken Curry', '- 500g chicken breasts\n- 2 onions, chopped\n- 3 garlic cloves, minced\n- 1 tbsp curry powder\n- 400ml coconut milk\n- 1 tbsp vegetable oil\n- Salt to taste', '1. Heat oil in a pan and saut√© onions and garlic until golden.\n2. Add chicken pieces and cook until lightly browned.\n3. Stir in curry powder and cook for 1 minute.\n4. Pour in coconut milk and simmer for 20 minutes.\n5. Serve with rice or naan bread.', NULL, 2),
('Pancakes', '- 150g plain flour\n- 2 large eggs\n- 300ml milk\n- Pinch of salt\n- Butter for frying', '1. Whisk flour, eggs, and milk to create a smooth batter.\n2. Heat a little butter in a frying pan over medium heat.\n3. Pour in a small amount of batter and swirl to coat the pan.\n4. Cook for 1-2 minutes per side until golden.\n5. Serve with toppings of your choice.', NULL, 3),
('Vegetable Stir-fry', '- 2 cups mixed vegetables (broccoli, carrots, bell peppers)\n- 2 tbsp soy sauce\n- 1 tbsp sesame oil\n- 1 garlic clove, minced\n- 1 tsp ginger, grated', '1. Heat sesame oil in a wok or pan.\n2. Add garlic and ginger, cooking until fragrant.\n3. Add mixed vegetables and stir-fry for 5-7 minutes.\n4. Drizzle with soy sauce and toss to combine.\n5. Serve as a side dish or over rice.', NULL, NULL);
